import { clear } from 'console';
import React, { createContext, useState, useEffect } from 'react';

import { addItemsToCart, removeItemsToCart, clearItemsFromCart } from './cart.utils';

export const CartContext = createContext({
  hidden: true,
  toggleHidden: () => {},
  cartItems: [],
  addItem: () => {},
  removeItem: () => {},
  clearItem: () => {},
  cartItemsCount: 0,
});

const CartProvider = ({ children }) => {
  const [hidden, setHidden] = useState(true);
  const [cartItems, setCartItems] = useState([]);
  const [cartItemsCount, setCartItemsCount] = useState(0);

  const addItem = (item) => setCartItems(addItemsToCart(cartItems, item));
  const removeItem = (item) => setCartItems(removeItemsToCart(cartItems, item));
  const clearItem = (item) =>
    setCartItems(clearItemsFromCart(cartItems, item));
  const toggleHidden = () => setHidden(!hidden);

  useEffect(() => {
    setCartItemsCount(cartItems.length);
  },[cartItems])

  return (
    <CartContext.Provider
      value={{
        hidden,
        toggleHidden,
        cartItems,
        addItem,
        cartItemsCount,
        removeItem,
        clearItem,
      }}
    >
      {children}
    </CartContext.Provider>
  );
};

export default CartProvider;
