import React from 'react'
import {connect} from 'react-redux'
// import {selectCollection} from '../../redux/shop/shop.selector'
import withRouterWrapper from '../../components/withRouter.component'
import CollectionItem from '../../components/collection-item/collection-item.component'
import {checkUserSession} from '../../redux/user/user-actions'
import CollectionsContext from '../../context/collections/collections.context'

import './collection.component.styles.scss'

const CollectionPage = ({ checkUserSession, ownProps }) => {
  checkUserSession();
  return (
    <CollectionsContext.Consumer>
      {(collections) => {
        const {title, items} = collections[ownProps.match.params.categoryId];
        <div className="collection-page">
          <h2 className="title">{title}</h2>
          <div className="items">
            {items.map((item) => (
              <CollectionItem key={item.id} item={item} />
            ))}
          </div>
        </div>
      }}
    </CollectionsContext.Consumer>
  )
}

export default withRouterWrapper(
  connect(null, mapDispatchToProps)(CollectionPage)
)
